import { observable, action } from 'mobx';

class CounterStore {
  @observable
  count = 5;

  @action      // state를 바꿔주는 action 
  increament() {
    this.count++;
  }

  @action
  decrement() {
    this.count--;
  }
}
// 위의 데이터를 컴포넌트에서 state로 활용할 수 있는지 보려면 export 할때 new 를 붙여준다
export default new CounterStore();
